""" Site search module URLs """__author__ = "Gage Tseng <gage.tseng@geniecapital.com>"__version__ = "$Id:$"from django.conf.urls.defaults import *urlpatterns = patterns('search.ajax',#	url(r'^ajax/search_all', 'search_all', name="search-all"),	url(r'^ajax/(?P<model>dish|place|all)/search_name', 'search_name', name="search-name"),	url(r'^ajax/(?P<model>dish|place|all)/auto_complete_name', 'auto_complete_name', name="auto-complete-name"),	url(r'^ajax/auto_complete_main', 'auto_complete_main', name="auto-complete-main"),	url(r'^ajax/auto_complete_people/$', 'auto_complete_people', name="auto-complete-people"),	url(r'^ajax/auto_complete_people/(?P<selector>all|site)', 'auto_complete_people', name="auto-complete-people"),	url(r'^ajax/auto_complete_place', 'auto_complete_place', name="auto-complete-place"),	url(r'^ajax/auto_complete_event', 'auto_complete_event', name="auto-complete-event"),	url(r'^ajax/auto_complete_dish/(?P<place_id>\w+)', 'auto_complete_dish', name="auto-complete-dish"),	url(r'^ajax/auto_complete_address', 'auto_complete_address', name="auto-complete-address"),	url(r'^ajax/auto_complete_city', 'auto_complete_city', name="auto-complete-city"),	url(r'^ajax/related_contact/(?P<selector>all|site|no_site)', 'related_contact', name="auto-related-contact"),	url(r'^ajax/search_filter', 'search_filter', name="search-filter"),	url(r'^ajax/reset_user_locaion', 'reset_user_locaion', name="reset-user-locaion"),)urlpatterns += patterns('search.views',	url(r'^$', 'search', name="search-result"),)